import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import { useState } from "react";

function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("2023");
  const [filteredInfoText, setFilteredInfoText] = useState('2024, 2025 & 2026')

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);

    if(selectedYear === '2023'){
        setFilteredInfoText('2024, 2025 & 2026');
    }
    else if(selectedYear ===  '2024'){
        setFilteredInfoText('2023, 2025 & 2026');
    }
    else if(selectedYear ===  '2025'){
        setFilteredInfoText('2023, 2024 & 2026');
    }
    else{
        setFilteredInfoText('2023, 2024 & 2025');
    }
  };

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter selected = {filteredYear} onFilterChange={filterChangeHandler} />
        <p>The data of years {filteredInfoText} is hidden</p>
        <ExpenseItem
          title={props.items[0].title}
          amount={props.items[0].amount}
          date={props.items[0].date}
        ></ExpenseItem>

        <ExpenseItem
          title={props.items[1].title}
          amount={props.items[1].amount}
          date={props.items[1].date}
        ></ExpenseItem>

        <ExpenseItem
          title={props.items[2].title}
          amount={props.items[2].amount}
          date={props.items[2].date}
        ></ExpenseItem>

        <ExpenseItem
          title={props.items[3].title}
          amount={props.items[3].amount}
          date={props.items[3].date}
        ></ExpenseItem>
      </Card>
    </div>
  );
}

export default Expenses;
