import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import { useState } from "react";

function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("2023");
  let filteredInfoText = "2024, 2025 & 2026";

  if (filteredYear === "2024") {
    filteredInfoText = "2023, 2025 & 2026";
  } else if (filteredYear === "2025") {
    filteredInfoText = "2023, 2024 & 2026";
  } else {
    filteredInfoText = "2023, 2024 & 2025";
  }

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onFilterChange={filterChangeHandler}
        />
        <p>The data of years {filteredInfoText} is hidden</p>
        {props.items.map(expense => {
          <ExpenseItem
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />;
        })};
      </Card>
    </div>
  );
}

export default Expenses;
